jQuery(document).ready(function($){$('.monograph-details a:not(.print, .btn-expand)').each(function(){var a=new RegExp('/'+window.location.host+'/');if(!a.test(this.href)){$(this).click(function(event){event.preventDefault();event.stopPropagation();window.open(this.href,'_blank')})}});$('.image-carousel-nav ul').slickLightbox({itemSelector:'li > a'});$('.fade').addClass('show');var pCount=Math.floor($('#uses p').length);var imgCount=$('.in-content-images > img').length;var pNum=0;$('.in-content-images > img').each(function(){if($(this).hasClass('image0')){$('#uses').prepend($(this))}else{pNum=Math.floor(pCount/imgCount);$('#uses p').eq(pNum).after($(this));imgCount--}});$('.btn-expand').click(function(){if($(this).hasClass('collapsed')){$(this).html('Show More')}else{$(this).html('Show Less')}});$('.btn-sort, .close').click(function(){$('.taxonomy-search').toggle('fast');return!1});$('.taxonomy-search select').change(function(){var slug=$(this).attr('name').replace('cat_','');var value=$(this).val();var host='https://'+window.location.hostname;window.location.href=host+'/monograph_'+slug+'/'+value+'/'})})